<script setup>
import { ref, onMounted } from "vue";
import store from "../../store";

function getdata() {
  let list = store.state.mapList;
  list.forEach((item) => {
    maps(item.mapName, item.color);
    if (item.children) {
      item.children.forEach((child) => {
        maps(child.mapName, child.color);
      });
    }
  });
}

function maps(mapName, rgbS) {
  const path = document.getElementById(mapName);
  if (path) {
    if (path.style.fill != rgbS) path.style.fill = rgbS;
    else path.style.fill = "";
  }
}

function handleCityClick(cityName) {
  if (cityName == "广东省") store.state.selectedMapComponent = "广东省";
}

onMounted(() => {
  getdata();
});
</script>

